import React,{useState} from 'react'

const App = () => {
   const [Details,setDetails]=useState({
      fname:"",
      lname:"",
      email:"",
   })
   const InputEvent=(event)=>{
      const name=event.target.name;
      const value=event.target.value;
      setDetails((value,name)=>{
          return {
             name
          }
      });
     

   }
   const onSubmit=(e)=>{
      e.preventDefault();
      console.log("clicked");

   }
   return (
      <>
      <div className="wrapper">
         <div className="inner">
      <form onClick={onSubmit}>
            <h1>hello {Details.fname} {Details.lname}</h1>
            <input autoComplete="off" name="fname" onChange={InputEvent} type="text" placeholder="firstName"/>
            <input autoComplete="off" name="lname" onChange={InputEvent} type="text" placeholder="Lastname"/>
            <input autoComplete="off" name="email" onChange={InputEvent} type="email" placeholder="email"/>
            <input  type="submit"/>
            </form>
         </div>
      </div>
         
      </>
   )
}

export default App
