import React,{useState} from 'react'

const App = () => {
   const [Details,setDetails]=useState({
      fname:"",
      lname:"",
   })
   const InputEvent=(event)=>{
      const name=event.target.name;
      const value=event.target.value;
      setDetails((oldValue)=>{
         if(name=="fname") {
            return{
               fname:value,
               lname:oldValue.lname,
            }
         }else if(name=="name") {
            return{
               fname:value,
               lname:oldValue.lname,
            }
         }

      })
     

   }
   const onSubmit=(e)=>{
      e.preventDefault();
      console.log("clicked");

   }
   return (
      <>
      <div className="wrapper">
         <div className="inner">
      <form onClick={onSubmit}>
            <h1>hello {Details.fname} {Details.lname}</h1>
            <input value={Details.fname} autoComplete="off" name="fname" onChange={InputEvent} type="text" placeholder="firstName"/>
            <input value={Details.lname} autoComplete="off" name="lname" onChange={InputEvent} type="text" placeholder="Lastname"/>
           <input  type="submit"/>
            </form>
         </div>
      </div>
         
      </>
   )
}

export default App
