import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";
import { FormControl, Input, InputLabel } from "@material-ui/core";
import Message from "./Message";
import db from "./firebase";
import firebase from "firebase"
import Flipmove from "react-flip-move"

const App = () => {
   const [input, setInput] = useState("");
   const [message, setMessage] = useState([]);
   const [username, setUsername] = useState("");

   useEffect(()=>{
       db.collection("messages").orderBy("timestamp","desc").onSnapshot(onsnapshot=>{
          setMessage(onsnapshot.docs.map(doc=>({id:doc.id,data:doc.data()})))
       });
   },[])
   useEffect(() => {
      setUsername(prompt("Username:"));
   }, []);


   const SendMessage = (event) => {
      event.preventDefault();

      // Add into database
      db.collection("messages").add({
         message:input,
         username:username,
         timestamp:firebase.firestore.FieldValue.serverTimestamp()
      })


      setMessage((olditem) => {
         return [...olditem, { username: username, message: input }];
      });
      setInput("");
   };

   return (
      <div className="forAlign">
         <h2>Facebook Messenger-CloneğŸš€</h2>
         <h3>Hello {username}</h3>
         <br />
         <form>
            <FormControl>
               <InputLabel htmlFor="my-input">Send Message</InputLabel>
               <Input
                  value={input}
                  onChange={(event) => setInput(event.target.value)}
               />
            </FormControl>

            <Button
               disabled={!input}
               type="submit"
               onClick={SendMessage}
               variant="contained"
               color="primary"
            >
               Send message
            </Button>
         </form>
        
        <Flipmove>
         {message.map((message) => {
            return <Message key={index} username={username} message={message} />;
         })}
         </Flipmove>
      </div>
   );
};

export default App;
