import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";
import { FormControl, Input, InputLabel } from "@material-ui/core";
import Message from "./Message";
import db from "./firebase";

const App = () => {
   const [input, setInput] = useState("");
   const [message, setMessage] = useState([{username:"Sandeep",message:"hey"},{username:"sandy",message:"buddy"}]);
   const [username, setUsername] = useState("");

   useEffect(()=>{
       db.collection("messages").onSnapshot(onsnapshot=>{
          setMessage(onsnapshot.docs.map(doc=>doc.data()))
       });
   },[])
   useEffect(() => {
      setUsername(prompt("Username:"));
   }, []);
   const SendMessage = (event) => {
      event.preventDefault();
      setMessage((olditem) => {
         return [...olditem, { username: username, message: input }];
      });
      setInput("");
   };

   return (
      <div className="forAlign">
         <h2>Facebook -Messenger-CloneğŸš€</h2>
         <h3>Hello {username}</h3>
         <br />
         <form>
            <FormControl>
               <InputLabel htmlFor="my-input">Send Message</InputLabel>
               <Input
                  value={input}
                  onChange={(event) => setInput(event.target.value)}
               />
            </FormControl>

            <Button
               disabled={!input}
               type="submit"
               onClick={SendMessage}
               variant="contained"
               color="primary"
            >
               Send message
            </Button>
         </form>

         {message.map((message, index) => {
            return <Message username={username} message={message} />;
         })}
      </div>
   );
};

export default App;
